INSERT INTO `movie` VALUES ('149', '反贪风暴4', 'https://p1.meituan.net/movie/c63849c7a9de360a7b192bc322792a111705236.jpg@464w_644h_1e_1c', '剧情,动作,犯罪', '中国香港,中国大陆', '98', '2019-04-04', '90', '381000', '795000000', '廉政公署收到报案人廖雨萍（周秀娜 饰）的实名举报，举报正在坐牢的富二代曹元元（林峯 饰）涉嫌行贿监狱里的监督沈济全（谭耀文 饰）以及惩教员，首席调查主任陆志廉（古天乐 饰）决定深入虎穴，卧底狱中。在监狱里，被陆志廉送入监狱的前警司黄文彬（林家栋 饰）以及曹元元两大帮派势成水火，陆志廉趁机接近曹元元取得信任。同时监狱外的廉政公署总调查主任程德明（郑嘉颖 饰）、国内反贪局行动处处长洪亮（丁海峰 饰）也陆港联手，通力合作，最终成功破获贪腐行贿大案。', '\n      林德禄\n    ');
